DECLARE @d DATETIME = GETDATE();
SELECT FORMAT ( @d, 'd', 'en-US' ) AS 'US English Short date'
      ,FORMAT ( @d, 'd', 'en-gb' ) AS 'Great Britain Short date'
      ,FORMAT ( @d, 'd', 'fr-fr' ) AS 'French (France) short date'
      ,FORMAT ( @d, 'd', 'ar-dz' ) AS 'Arabic (Algeria) short date'
      ,FORMAT ( @d, 'd', 'de-de' ) AS 'German Short date'
      ,FORMAT ( @d, 'd', 'ru-ru' ) AS 'Russia short date'
      ,FORMAT ( @d, 'd', 'zh-cn' ) AS 'Simplified Chinese (PRC) Short date'; 

SELECT FORMAT ( @d, 'D', 'en-US' ) AS 'US English Long date'
      ,FORMAT ( @d, 'D', 'en-gb' ) AS 'Great Britain English Long date'
      ,FORMAT ( @d, 'D', 'fr-fr' ) AS 'French (France) Long date'
      ,FORMAT ( @d, 'D', 'ar-dz' ) AS 'Arabic (Algeria) Long date'
      ,FORMAT ( @d, 'D', 'de-de' ) AS 'German Long date'
      ,FORMAT ( @d, 'D', 'ru-ru' ) AS 'Russia Long date'
      ,FORMAT ( @d, 'D', 'zh-cn' ) AS 'Chinese (Simplified PRC) Long date';
      
      
